import React from "react";

const Home = () => {
  // Estilos CSS incorporados como objeto JavaScript
  const styles = {
    '@import': "url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap')",
    
    body: {
      fontFamily: "'Roboto', sans-serif"
    },
    
    header: {
      textAlign: 'center',
      animation: 'fadeIn 1s ease-in-out'
    },
    
    headerTitle: {
      fontSize: '3rem',
      color: 'white',
      marginBottom: '0.5rem',
      animation: 'slideIn 1s ease-in-out'
    },
    
    headerSubtitle: {
      fontSize: '1.25rem',
      color: '#f0f0f0',
      maxWidth: '600px',
      margin: '0 auto',
      animation: 'fadeInUp 1s ease-in-out'
    },
    
    // Definir las animaciones con @keyframes
    '@keyframes fadeIn': {
      from: { opacity: 0 },
      to: { opacity: 1 }
    },
    
    '@keyframes slideIn': {
      from: { transform: 'translateY(-20px)', opacity: 0 },
      to: { transform: 'translateY(0)', opacity: 1 }
    },
    
    '@keyframes fadeInUp': {
      from: { transform: 'translateY(20px)', opacity: 0 },
      to: { transform: 'translateY(0)', opacity: 1 }
    }
  };

  // Crear un elemento style para incluir las animaciones y la importaci√≥n de fuentes
  React.useEffect(() => {
    const styleEl = document.createElement('style');
    styleEl.innerHTML = `
      @import url('https://fonts.googleapis.com/css2?family=Roboto:wght@400;700&display=swap');
      
      @keyframes fadeIn {
        from { opacity: 0; }
        to { opacity: 1; }
      }
      
      @keyframes slideIn {
        from { transform: translateY(-20px); opacity: 0; }
        to { transform: translateY(0); opacity: 1; }
      }
      
      @keyframes fadeInUp {
        from { transform: translateY(20px); opacity: 0; }
        to { transform: translateY(0); opacity: 1; }
      }
    `;
    document.head.appendChild(styleEl);
    
    return () => {
      document.head.removeChild(styleEl);
    };
  }, []);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-100 p-4" style={{ fontFamily: "'Roboto', sans-serif" }}>
      <header className="text-4xl font-bold text-gray-800 mb-4" style={{ 
        textAlign: 'center',
        animation: 'fadeIn 1s ease-in-out'
      }}>Bienvenido a EducStation</header>
      <p className="text-lg text-gray-600 max-w-2xl text-center" style={{
        animation: 'fadeInUp 1s ease-in-out'
      }}>
        Descubre un espacio dedicado al aprendizaje, con art√≠culos sobre educaci√≥n, estrategias de estudio y herramientas innovadoras para potenciar tu conocimiento.
      </p>
      <div className="mt-8 text-center">
        <h2 className="text-2xl font-bold text-gray-800 mb-4" style={{
          animation: 'slideIn 1s ease-in-out'
        }}>üìö Secciones del EducStation Web Blog</h2>
        <ul className="text-lg text-gray-700">
          <li>üì¢ Noticias</li>
          <li>üìñ T√©cnicas de Estudio</li>
          <li>‚ùì Problem√°ticas en el Estudio</li>
          <li>üéì Educaci√≥n de Calidad</li>
          <li>üõ† Herramientas Tecnol√≥gicas</li>
          <li>üöÄ Desarrollo Profesional Docente</li>
          <li>ü§ù Comunidad y Colaboraci√≥n</li>
        </ul>
      </div>
      <button className="mt-6 px-6 py-2 bg-blue-500 text-white rounded-lg shadow-md hover:bg-blue-600 transition">
        Leer art√≠culos
      </button>
    </div>
  );
};

export default Home;
